mapper.select:
select a.大区 ,SUM(a.分红比例) 'sum' from ( select b.FContractTime ,a.FContract ,x.Name '业务员' ,c.FDividendRatio '分红比例' ,f.FPlateNum '车牌' ,case when d.Name = '苏州上海办事处' then '上海营销一部' when d.Name = '湘潭营销一部' then '湖南营销一部' else d.Name end '部' ,case when d.Name = '苏州上海办事处' then '上海' when d.Name = '湘潭营销一部' then '湖南' else left(hh.Name,2) end '城市' ,case when d.Name = '苏州上海办事处' then '苏沪省区' when d.Name = '湘潭营销一部' then '湘赣省区' else jh.Name end '省级' ,case when d.Name = '苏州上海办事处' then '长三角战区' when d.Name = '湘潭营销一部' then '华南战区' else j.Name end '大区' from FinLease_Contract a join FM_ConAcc b on a.FContractID = b.ID join FinLease_Contract_FClerkList c on a.ID = c.FinLease_Contract_ID join lbOrganization d on c.FOrga = d.ID join Fcar f on a.FCar = f.ID join Entity_YJSJB h ON b.ID = h.FConAcc left join Entity_YJSJB e on b.ID = e.FConAcc left join tUser x on x.ID = c.FYWYMD left join lbOrganization g on d.FID = g.ID left join lbOrganization hh on g.FID = hh.ID left join lbOrganization jh on hh.FID = jh.ID left join lbOrganization j on jh.FID = j.ID where isnull(b.FIsDrop,0)!=1 and e.FQuoType in (1,2) and isnull(a.WFState,0) != 3 and c.FDividendRatio != 0 and case when d.Name = '苏州上海办事处' then '上海' else LEFT(d.Name,2) end != '淘汽' and d.Name not like '%营销部%' and CONVERT(varchar(4),b.FContractTime,120) >= #{s_year}
union all
select b.FContractTime ,a.FContract ,x.Name '业务员' ,c.FDividendRatio '分红比例' ,f.FPlateNum '车牌' ,d.Name '部' ,left(g.Name,2) '城市' ,hh.Name '省级' ,jh.Name '大区' from FinLease_Contract a join FM_ConAcc b on a.FContractID = b.ID join FinLease_Contract_FClerkList c on a.ID = c.FinLease_Contract_ID join lbOrganization d on c.FOrga = d.ID join Fcar f on a.FCar = f.ID join Entity_YJSJB h ON b.ID = h.FConAcc left join Entity_YJSJB e on b.ID = e.FConAcc left join tUser x on x.ID = c.FYWYMD left join lbOrganization g on d.FID = g.ID left join lbOrganization hh on g.FID = hh.ID left join lbOrganization jh on hh.FID = jh.ID where isnull(b.FIsDrop,0)!=1 and e.FQuoType in (1,2) and isnull(a.WFState,0) != 3 and c.FDividendRatio != 0 and case when d.Name = '苏州上海办事处' then '上海' else LEFT(d.Name,2) end != '淘汽' and d.Name like '%营销部%' and CONVERT(varchar(4),b.FContractTime,120) >= #{s_year} ) a where a.大区 like '%战区%' group by a.大区 order by SUM(a.分红比例) desc
